mapper.select:
SELECT EmployeeID AS username, SocialNumber AS password, Role AS role
FROM User
WHERE EmployeeID = #{username}

SELECT u.EmployeeID, u.Name, u.State, u.Role, u.birthday, u.SocialNumber, u.EmailAddress, e.year, e.month, e.BasicSalary, e.HolidayAllowance, e.LunchExpenses, e.FirstWeekHolidayAllowance, e.RetroactiveHolidayAllowance, e.TotalPayment, e.IncomeTax, e.ResidentTax, e.EmploymentInsurance, e.NationalPension, e.HealthInsurance, e.ElderlyCareInsurance, e.EmploymentInsuranceDeduction, e.NationalPensionDeduction, e.HealthInsuranceDeduction, e.ElderlyCareInsuranceDeduction, e.DeductionTotal, e.NetPayment, e.TotalWorkDays, e.TotalWorkingHours, e.HolidayCalculationHours, e.OvertimeCalculationHours, e.HourlyWage, e.LunchAllowance FROM User u JOIN EmployeeSalary e ON u.EmployeeID = e.EmployeeID
AND e.year = #{year}
AND e.month = #{month}
AND u.Name LIKE CONCAT('%', #{name}, '%')
AND CAST(u.EmployeeID AS CHAR) LIKE CONCAT('%', #{employeeID}, '%')
ORDER BY e.year DESC, e.month DESC

SELECT year, month
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID} AND year = #{year} AND month = #{month}

SELECT year, month
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID}
ORDER BY year DESC, month DESC
    LIMIT #{limit} OFFSET #{offset}

SELECT year, month
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID} AND year = #{year}
ORDER BY year DESC, month DESC
    LIMIT #{limit} OFFSET #{offset}

SELECT year, month
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID} AND month = #{month}
ORDER BY year DESC, month DESC
    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID}

SELECT COUNT(*)
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID} AND year = #{year}

SELECT COUNT(*)
FROM EmployeeSalary
WHERE EmployeeSalary.EmployeeID = #{employeeID} AND month = #{month}

mapper.delete:
DELETE FROM EmployeeSalary
WHERE EmployeeID IN

    #{id}

DELETE FROM User
